import sys

def scan_directory(directory):
    file_types = {}
    for entry in ctx.os.scandir(directory):
        if entry.is_dir(follow_symlinks=False):
            sub_directory_results = scan_directory(entry.path)
            for key, value in sub_directory_results.items():
                if key in file_types:
                    file_types[key] += value
                else:
                    file_types[key] = value
        elif entry.is_file(follow_symlinks=False):
            extension = entry.name.split('.')[-1] if '.' in entry.name else 'no_extension'
            if extension in file_types:
                file_types[extension] += 1
            else:
                file_types[extension] = 1
    return file_types

