class Cart:
    def __init__(self):
        self.items = {}

    def add_product(self, product, quantity):
        if product.name in self.items:
            self.items[product.name]['quantity'] += quantity
        else:
            self.items[product.name] = {'price': product.price, 'quantity': quantity}

    def remove_product(self, product, quantity):
        if product.name in self.items:
            if self.items[product.name]['quantity'] > quantity:
                self.items[product.name]['quantity'] -= quantity
            elif self.items[product.name]['quantity'] == quantity:
                del self.items[product.name]

    def total_price(self):
        total = 0
        for item in self.items.values():
            total += item['price'] * item['quantity']
        return total

    def summary(self):
        for product, details in self.items.items():
            print(f"{product}: {details['quantity']} x {details['price']} = {details['quantity'] * details['price']}")

